public class FindAllAnagramsInAString {

    public static void main(String[] args) {

    }

    private static boolean reduce(String substring, String sub) {
        HashMap<Character, Integer> source = new HashMap<>();
        HashMap<Character, Integer> target = new HashMap<>();

        for (int i = 0; i < sub.length(); i++) {
            if (source.containsKey(sub.charAt(i))) {
                source.put(sub.charAt(i), source.get(sub.charAt(i) + 1));
            } else {
                source.put(sub.charAt(i), 1);
            }
        }
        for (int i = 0; i < substring.length(); i++) {
            if (target.containsKey(substring.charAt(i))) {
                target.put(substring.charAt(i), target.get(substring.charAt(i) + 1));
            } else {
                target.put(substring.charAt(i), 1);
            }
        }
        return source.equals(target);
    }

    private static boolean reduce(Map<Character, Integer> map, String substring) {
        Map<Character, Integer> localMap = new HashMap<>();
        for (Character c : substring.toCharArray()) {
            if (map.containsKey(c)) {
                localMap.put(c, localMap.getOrDefault(c, 0) + 1);
            } else {
                break;
            }
        }
        return localMap.equals(map);
    }

    @Test
    public void test() {
        assertEquals(Arrays.asList(0, 1, 2), findAnagrams("abab", "ab"));
        assertEquals(Arrays.asList(), findAnagrams("ab", "cd"));
        assertEquals(Arrays.asList(), findAnagrams("af", "be"));
        assertEquals(Arrays.asList(1), findAnagrams("baa", "aa"));
        assertEquals(Arrays.asList(0, 6), findAnagrams("cbaebabacd", "abc"));
        assertEquals(Arrays.asList(0, 2, 4, 6), findAnagrams(
                "ababababab", "aab"));

        List<Integer> anagrams = findAnagrams(""
                , "");


    }

    public List<Integer> findAnagramsOld(String tar, String arg) {
        List<Integer> result = new ArrayList<>();
        for (int i = 0; i <= tar.length() - arg.length(); i++) {
            if (reduce(tar.substring(i, i + arg.length()), arg)) {
                result.add(i);
            }
        }
        return result;
    }

    public List<Integer> findAnagrams(String tar, String arg) {
        List<Integer> result = new ArrayList<>();
        Map<Character, Integer> map = new HashMap<>();
        for (Character c : arg.toCharArray()) {
            map.put(c, map.getOrDefault(c, 0) + 1);
        }
        int len = arg.length();
        int res = tar.length() - arg.length();
        Map<Character, Integer> localMap = new HashMap<>();
        for (int i = 0; i <= res; i++) {
            String cur = tar.substring(i, i + len);

            if (i == 0) {
                for (Character c : cur.toCharArray()) {
                    localMap.put(c, localMap.getOrDefault(c, 0) + 1);
                }
                if (localMap.equals(map)) {
                    result.add(i);
                }
            } else {
                int lastIndex = i - 1;
                if (localMap.get(tar.charAt(lastIndex)) == 1) {
                    localMap.remove(tar.charAt(lastIndex));
                } else {
                    localMap.put(tar.charAt(lastIndex), localMap.get(tar.charAt(lastIndex)) - 1);
                }
                //  abab;

                localMap.put(tar.charAt(i + len - 1), localMap.getOrDefault(tar.charAt(i + len - 1), 0) + 1);
                if (localMap.equals(map)) {
                    result.add(i);
                }
            }
        }
        return result;
    }
}

